$(document).ready(function(){$('#sendMailBtn').click(function(e){$.ajax({type:"POST",url:"/admin/sent/sendMail",data:{recipient:document.getElementById("recipient").value,cc:document.getElementById("cc").value,bcc:document.getElementById("bcc").value,subject:document.getElementById("subject").value,message:quill.container.firstChild.innerHTML},success:function(data){$('#recipient').val('');$('#cc').val('');$('#bcc').val('');$('#subject').val('');quill.container.firstChild.innerHTML='';$('#mail-response-success').css('display','block')},error:function(data){console.log('Sending email failed');alert('We encountered some problem while sending this email')}})})})